exports.getGuestWebContents = (guestInstanceId) => {
  const command = 'ELECTRON_BROWSER_GUEST_WEB_CONTENTS'
  const meta = ipcRenderer.sendSync(command, guestInstanceId)
  return metaToValue(meta)
